import { initializeApp } from "firebase/app";
import {
  getAuth,
  signInWithRedirect,
  signInWithPopup,
  GoogleAuthProvider,
} from "firebase/auth";

import {} from "firebase/firestore";

const firebaseConfig = {
  apiKey: "AIzaSyBFoUZinzuWEg-ZuS18UR1jyOtQiIiZZ50",
  authDomain: "crwn-clothing-db-22f78.firebaseapp.com",
  projectId: "crwn-clothing-db-22f78",
  storageBucket: "crwn-clothing-db-22f78.appspot.com",
  messagingSenderId: "69835503100",
  appId: "1:69835503100:web:055d5be37fe69d93f76e79",
};

const firebaseApp = initializeApp(firebaseConfig);

const provider = new GoogleAuthProvider();
provider.setCustomParameters({
  prompt: "select_account",
});

export const auth = getAuth();
export const signInWithGooglePopup = () => signInWithPopup(auth, provider);
